import Sidebar from '../comman/Sidebar';
import Header from '../comman/Header';
import { Link } from 'react-router-dom';
import { getAllBankDetail, blockAgent, unblockAgent } from "../../api";


function AllBankDetail() {
    const [bankData, setBankData] = useState([]);

    const handleAllAgent = async (event) => {
        try {
            const result = await getAllBankDetail();
            console.log(result.data);
            setBankData(result.data);
        } catch (error) {
            console.error("Error registering user:", error);
        }
    };

    const handleDeleteAccount = async (accountNumber) => {
        try {
          const result = await deleteAccount(accountNumber);
          console.log(result);
        } catch (error) {
          console.error('Error during account deletion:', error.message);
        }
      };
    const handleBlockAgent = async (username) => {
        try {
            await blockAgent(username);
            window.location.reload();
        } catch (error) {
            console.error('Error blocking user:', error);
        }
    };


    useEffect(() => {
        handleAllAgent();
    }, []);
    return (
        <div>
            <body id="page-top">
                {/*  <!-- Page Wrapper --> */}
