                                        <div className="table-container" style={{ overflowX: 'auto' }}>
                                            <div className="row">
                                                <div className="col">
                                                    <h5 className="pb-2 mb-0">All Bank List</h5>
                                                </div>
                                            </div>
                                            <div className="d-flex text-muted">
                                                <table className="table">
                                                    <thead>
                                                        <tr>
                                                            <th>Bank Name</th>
                                                            {/* <th>Name</th> */}
                                                            <th>Account No.</th>
                                                            <th>Account Holder Name</th>
                                                            <th></th>
                                                        </tr>
                                                    </thead>
                                                    <tbody>
                                                        {bankData.map((row) => (
                                                            <tr key={row.id}>
                                                                {/* <td><Link to={`/agent-details/${row.userName}`}>{row.userName}</Link></td> */}
                                                                <td>{row.bankName}</td>
                                                                <td>{row.bankAccountNumber}</td>
                                                                <td>{row.accountHolderName}</td>
                                                                <button type="button" className="btn btn-danger" onClick={() => handleDeleteAccount(row.bankAccountNumber)}>
                                                                    Delete
                                                                </button>
                                                                {/* <td>{row.agentBlocked ? (
                                                                    <button type="button" className="btn btn-success" onClick={() => handleUnblockAgent(row.userName)}>
                                                                        Unblock
                                                                    </button>
                                                                ) : (
                                                                    <button type="button" className="btn btn-danger" onClick={() => handleBlockAgent(row.userName)}>
                                                                        Block
                                                                    </button>
                                                                )}</td> */}
                                                            </tr>
                                                        ))}
                                                    </tbody>
                                                </table>
                                            </div>
