import React, { useState } from 'react';
import Sidebar from '../comman/Sidebar';
import Header from '../comman/Header';
import { reduceBalanceToUser } from '../../api';


function ReduceBalance() {
    const [formData, setFormData] = useState({
        userName: '',
        balance: '',
    });

    const [errors, setErrors] = useState({
        userName: '',
        balance: '',
    });

    const validateForm = () => {
        let valid = true;
        const newErrors = { ...errors };

        // Validate user name
        if (formData.userName.trim() === '') {
            newErrors.userName = 'User name is required';
            valid = false;
        } else {
            newErrors.userName = '';
        }

        // Validate first name
        if (formData.balance.trim() === '') {
            newErrors.balance = 'Balance is required';
            valid = false;
        } else {
            newErrors.balance = '';
        }

        setErrors(newErrors);
        return valid;
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        if (validateForm()) {
            // Proceed with form submission
            handleRegister();
            console.log('Form submitted:', formData);
        } else {
            console.log('Form has validation errors');
        }
    };

    const handleChange = (event) => {
        const { name, value } = event.target;
        setFormData({ ...formData, [name]: value });
    };

    const handleRegister = async () => {
        try {
            formData.balance = parseFloat(formData.balance);
            const result = await reduceBalanceToUser(formData);
            console.log(result);
        } catch (error) {
            console.error('Error during registration:', error.message);
        }
    };

    return (
        <div>
            <body id="page-top">
                <div id="wrapper">
                    <Sidebar />
                    <div id="content-wrapper" className="d-flex flex-column">
                        <div id="content">
                            <Header />
                            <div className="container-fluid">
                                <div className="d-sm-flex align-items-center justify-content-between mb-4">
                                    <h1 className="h3 mb-0 text-gray-800">Reduce Balance To User</h1>
                                </div>
                                <div className="row">
                                    <div className='col-lg-12 mb-4'>
                                        <form className="login-form" onSubmit={handleSubmit}>
                                            <div className="form-outline mb-4">
                                                <label className="form-label" htmlFor="formUserName">User Name</label>
                                                <input
                                                    type="text"
                                                    id="formUserName"
                                                    className={`form-control form-control-lg ${errors.userName && 'is-invalid'}`}
                                                    placeholder="Enter a user name"
                                                    name="userName"
                                                    value={formData.userName}
                                                    onChange={handleChange}
                                                />
                                                {errors.userName && <div className="invalid-feedback">{errors.userName}</div>}
                                            </div>
                                            <div className="form-outline mb-4">
                                                <label className="form-label" htmlFor="formFirstName">Balance</label>
                                                <input
                                                    type="text"
                                                    id="formBalance"
                                                    className={`form-control form-control-lg ${errors.balance && 'is-invalid'}`}
                                                    placeholder="Enter a balance"
                                                    name="balance"
                                                    value={formData.balance}
                                                    onChange={handleChange}
                                                />
                                                {errors.balance && <div className="invalid-feedback">{errors.balance}</div>}
                                            </div>
                                            <div className="text-center text-lg-start mt-4 pt-2">
                                                <button type="submit" className="btn btn-primary btn-lg">Proceed</button>
                                            </div>
                                        </form>
                                    </div>
                                </div>
                            </div>
                        </div>
                        <footer className="sticky-footer bg-white">
                            <div className="container my-auto">
                                <div className="copyright text-center my-auto">
                                    <span>Copyright &copy; Your Website 2023</span>
                                </div>
                            </div>
                        </footer>
                    </div>
                </div>
                <a className="scroll-to-top rounded" href="#page-top">
                    <i className="fas fa-angle-up"></i>
                </a>
            </body>
        </div>
    )
}

export default ReduceBalance
