// api.js
import axios from 'axios';

const API_BASE_URL = 'http://localhost:80';

const handleResponse = (response) => {
  if (response.status !== 200) {
    throw new Error('Network response was not ok.');
  }
  return response.data;
};

export const loginUser = async (username, password) => {
  try {
    const requestOptions = {
      method: 'post',
      url: 'http://localhost:80/admin/admin-login', // Replace with your actual API endpoint
      headers: {
        'Content-Type': 'application/json',
      },
      data: {
        username: username,
        password: password,
      },
    };

    const response = await axios(requestOptions);
    return handleResponse(response);
  } catch (error) {
    throw new Error('Error during login.');
  }
};

export const addAdmi = async (username, password) => {
  try {
    const requestOptions = {
      method: 'post',
      url: 'http://localhost:80/admin/admin-login', // Replace with your actual API endpoint
      headers: {
        'Content-Type': 'application/json',
      },
      data: {
        username: username,
        password: password,
      },
    };

    const response = await axios(requestOptions);
    return handleResponse(response);
  } catch (error) {
    throw new Error('Error during login.');
  }
};

export const getAllUser = async () => {
  try {
    const requestOptions = {
      method: 'get',
      url: `${API_BASE_URL}/getalluser`,
    };

    const response = await axios(requestOptions);
    return handleResponse(response);
  } catch (error) {
    throw new Error('Error registering user.');
  }
};

export const getUserByUsername = async (userName) => {
  try {
    const requestOptions = {
      method: 'get',
      url: `${API_BASE_URL}/search/${userName}`,
    };

    const response = await axios(requestOptions);
    return handleResponse(response);
  } catch (error) {
    throw new Error('Error registering user.');
  }
};

export const blockUser = async (phoneNumber) => {
  try {
    const myHeaders = new Headers();
    myHeaders.append('Content-Type', 'application/json');

    const raw = JSON.stringify({
      phoneNumber,
      blocked: true,
    });

    const requestOptions = {
      method: 'POST',
      headers: myHeaders,
      body: raw,
      redirect: 'follow',
    };

    const response = await fetch(`${API_BASE_URL}/block-user`, requestOptions);
    const result = await response.text();
    return result;
  } catch (error) {
    throw new Error('Error blocking user.');
  }
};

export const unblockUser = async (phoneNumber) => {
  try {
    const myHeaders = new Headers();
    myHeaders.append('Content-Type', 'application/json');

    const raw = JSON.stringify({
      phoneNumber,
      blocked: true,
    });

    const requestOptions = {
      method: 'POST',
      headers: myHeaders,
      body: raw,
      redirect: 'follow',
    };

    const response = await fetch(`${API_BASE_URL}/unblock-user`, requestOptions);
    const result = await response.text();
    return result;
  } catch (error) {
    throw new Error('Error blocking user.');
  }
};
