import React, { useState } from 'react';
import Sidebar from '../comman/Sidebar';
import Header from '../comman/Header';

function AddAgent() {
    const [formData, setFormData] = useState({
        userName: '',
        firstName: '',
        lastName: '',
        phoneNo: '',
    });

    const [errors, setErrors] = useState({
        userName: '',
        firstName: '',
        lastName: '',
        phoneNo: '',
    });

    const validateForm = () => {
        let valid = true;
        const newErrors = { ...errors };

        // Validate user name
        if (formData.userName.trim() === '') {
            newErrors.userName = 'User name is required';
            valid = false;
        } else {
            newErrors.userName = '';
        }

        // Validate first name
        if (formData.firstName.trim() === '') {
            newErrors.firstName = 'First name is required';
            valid = false;
        } else {
            newErrors.firstName = '';
        }

        // Validate last name
        if (formData.lastName.trim() === '') {
            newErrors.lastName = 'Last name is required';
            valid = false;
        } else {
            newErrors.lastName = '';
        }

        // Validate phone number
        if (formData.phoneNo.trim() === '') {
            newErrors.phoneNo = 'Phone number is required';
            valid = false;
        } else {
            newErrors.phoneNo = '';
        }

        setErrors(newErrors);
        return valid;
    };

    const handleSubmit = (event) => {
        event.preventDefault();
        if (validateForm()) {
            // Proceed with form submission
            console.log('Form submitted:', formData);
        } else {
            console.log('Form has validation errors');
        }
    };

    const handleChange = (event) => {
        const { name, value } = event.target;
        setFormData({ ...formData, [name]: value });
    };
    return (
        <div>
            <body id="page-top">
                {/*  <!-- Page Wrapper --> */}
                <div id="wrapper">
                    {/*  <!-- Sidebar --> */}
                    <Sidebar />
                    {/*  <!-- End of Sidebar --> */}
                    {/*  <!-- Content Wrapper --> */}
                    <div id="content-wrapper" className="d-flex flex-column">
                        {/*  <!-- Main Content --> */}
                        <div id="content">
                            {/*  <!-- Topbar --> */}
                            <Header />
                            {/*  <!-- End of Topbar --> */}
                            {/* <!-- Begin Page Content --> */}
                            <div className="container-fluid">
                                {/*  <!-- Page Heading --> */}
                                <div className="d-sm-flex align-items-center justify-content-between mb-4">
                                    <h1 className="h3 mb-0 text-gray-800">Add Agent</h1>

                                </div>
                                {/*   <!-- Content Row --> */}
                                <div className="row">
                                    {/*   <!-- Content Column --> */}
                                    <div className='col-lg-12 mb-4'>
                                    <form className="login-form" onSubmit={handleSubmit}>
                        {/* ... (your existing form fields) ... */}
                        <div className="form-outline mb-4">
                            <label className="form-label" htmlFor="form3Example3">User Name</label>
                            <input
                                type="text"
                                id="form3Example3"
                                className={`form-control form-control-lg ${errors.userName && 'is-invalid'}`}
                                placeholder="Enter a user name"
                                name="userName"
                                value={formData.userName}
                                onChange={handleChange}
                            />
                            {errors.userName && <div className="invalid-feedback">{errors.userName}</div>}
                        </div>
                        {/* ... (repeat the above structure for other form fields) ... */}
                        <div className="text-center text-lg-start mt-4 pt-2">
                            <button type="submit" className="btn btn-primary btn-lg">Proceed</button>
                        </div>
                    </form>
                                    </div>
                                </div>
                            </div>
                            {/*   <!-- /.container-fluid --> */}
                        </div>
                        {/*   <!-- End of Main Content -->
                               <!-- Footer --> */}
                        <footer className="sticky-footer bg-white">
                            <div className="container my-auto">
                                <div className="copyright text-center my-auto">
                                    <span>Copyright &copy; Your Website 2023</span>
                                </div>
                            </div>
                        </footer>
                        {/* <!-- End of Footer --> */}
                    </div>
                    {/*  <!-- End of Content Wrapper --> */}
                </div>
                {/*  <!-- End of Page Wrapper -->
                        <!-- Scroll to Top Button--> */}
                <a className="scroll-to-top rounded" href="#page-top">
                    <i className="fas fa-angle-up"></i>
                </a>
            </body>
        </div>
    )
}

export default AddAgent
