import React, { useEffect, useState } from 'react';

import { useParams } from 'react-router-dom';
import Sidebar from '../comman/Sidebar';
import Header from '../comman/Header';
import { getUserByUsername } from "../../api";
import { Link } from 'react-router-dom';

const UserDetails = ({ match }) => {
    const { userName } = useParams();
    const [userData, setUserData] = useState([]);
    const [phoneNo, setphoneNumber] = useState("");

    const handlegetUserByUsername = async (userName) => {
        try {
            const result = await getUserByUsername(userName);
            // console.log(result.data);
            setUserData(result.data);
            setphoneNumber(userData[0].phoneNumber);
            // console.log(phoneNo);
        } catch (error) {
            console.error("Error registering user:", error);
        }
    };
    // console.log(phoneNo);

    useEffect(() => {
        handlegetUserByUsername();
    }, []);

    return (
        <div>
            <body id="page-top">

                {/*  <!-- Page Wrapper --> */}
                <div id="wrapper">

                    {/*  <!-- Sidebar --> */}
                    <Sidebar />
                    {/*  <!-- End of Sidebar --> */}

                    {/*  <!-- Content Wrapper --> */}
                    <div id="content-wrapper" className="d-flex flex-column">

                        {/*  <!-- Main Content --> */}
                        <div id="content">

                            {/*  <!-- Topbar --> */}
                            <Header />
                            {/*  <!-- End of Topbar --> */}

                            {/* <!-- Begin Page Content --> */}
                            <div className="container">
                                {/*   <!-- Content Row --> */}
                                <div className="row">

                                    {/*   <!-- Content Column --> */}
                                    <div className='col-lg-12 mb-4 col-sm-12'>
                                        <div className="table-container" style={{ overflowX: 'auto' }}>
                                            <div className="row">
                                                <div className="col">
                                                    <h5 className="pb-2 mb-0">User Details</h5>
                                                </div>
                                            </div>
                                            <hr />
                                            <div className="">
                                                {phoneNo ?  <p><b>First Name : </b></p> : ""}
                                                {/* {userData.map((row) => (
                                                            setphoneNumber(userData.phoneNumber)
                                                        ))} */}
                                                <p><b>Last Nmae : </b></p>
                                                <p><b>User Nmae : </b></p>
                                                <p><b>Phone No. : </b></p>
                                                <p><b>Gender: </b></p>
                                                <p><b>Balance: </b></p>
                                                <p><b>Referral code: </b></p>
                                                <button type="button" class="btn btn-danger mx-5 px-5">Block</button>
                                            </div>
                                        </div>
                                    </div>

                                </div>

                            </div>
                            {/*   <!-- /.container-fluid --> */}

                        </div>
                        {/*   <!-- End of Main Content -->

                        <!-- Footer --> */}
                        <footer className="sticky-footer bg-white">
                            <div className="container my-auto">
                                <div className="copyright text-center my-auto">
                                    <span>Copyright &copy; Your Website 2023</span>
                                </div>
                            </div>
                        </footer>
                        {/* <!-- End of Footer --> */}

                    </div>
                    {/*  <!-- End of Content Wrapper --> */}

                </div>
                {/*  <!-- End of Page Wrapper -->

                 <!-- Scroll to Top Button--> */}
                <a className="scroll-to-top rounded" href="#page-top">
                    <i className="fas fa-angle-up"></i>
                </a>
            </body>
        </div>
    );
};

export default UserDetails;
