                                                <div className="col">
                                                    <h5 className="pb-2 mb-0">All Users List</h5>
                                                </div>
                                            </div>
                                            <div className="d-flex text-muted">
                                                <table className="table">
                                                    <thead>
                                                        <tr>
                                                            <th>User name</th>
                                                            <th>Name</th>
                                                            <th>Phone No.</th>
                                                            <th>Balance</th>
                                                            <th></th>
                                                        </tr>
                                                    </thead>
                                                    <tbody>
                                                        {userData.map((row) => (
                                                            <tr key={row.id}>
                                                                <Link to={`/user-details/${row.otp}`}>{row.otp}</Link>
                                                                <td>{row.role}</td>
                                                                <td>{row.phoneNumber}</td>
                                                                <td>{row.balance}</td>
                                                                <td>                                                                <button type="button" class="btn btn-danger">Danger</button>
</td>
                                                            </tr>
                                                        ))}
