import React, { useEffect, useState } from 'react';

import { useParams } from 'react-router-dom';
import Sidebar from '../comman/Sidebar';
import Header from '../comman/Header';
import { getUserByUsername } from "../../api";
import { Link } from 'react-router-dom';


const UserDetails = ({ match }) => {
    const { userName } = useParams();
    const [userData, setUserData] = useState([]);
    const [phoneNo, setPhoneNumber] = useState("");
    const [firstName, setFirstName] = useState("");
    const [lastName, setLastName] = useState("");
    const [gender, setGender] = useState("");
    const [referrerCode, setReferrerCode] = useState("");
    const [balance, setBalance] = useState("");
    const [userNa, setUserName] = useState("");

    const handlegetUserByUsername = async (userName) => {
      try {
        const result = await getUserByUsername(userName);
        setUserData(result.data);

        // Destructure user data for better readability
        const [user] = result.data;

        setPhoneNumber(user?.phoneNumber);
        setFirstName(user?.firstName);
        setLastName(user?.lastName);
        setGender(user?.gender);
        setReferrerCode(user?.referrerCode);
        setBalance(user?.balance);
        setUserName(user?.userName);
      } catch (error) {
        console.error("Error fetching user data:", error);
      }
    };

    useEffect(() => {
      handlegetUserByUsername();
    }, []);

    return (
      <div>
        {/* ... (your HTML structure) */}
        <div className="table-container" style={{ overflowX: 'auto' }}>
          <div className="row">
            <div className="col">
              <h5 className="pb-2 mb-0">User Details</h5>
            </div>
          </div>
          <hr />
          <div className="">
            {/* Conditionally render each piece of information */}
            {firstName && <p><b>First Name : {firstName} </b></p>}
            {lastName && <p><b>Last Name : {lastName} </b></p>}
            {userNa && <p><b>User Name : {userNa} </b></p>}
            {phoneNo && <p><b>Phone No. : {phoneNo} </b></p>}
            {gender && <p><b>Gender : {gender} </b></p>}
            {balance && <p><b>Balance : {balance} </b></p>}
            {referrerCode && <p><b>Referral code : {referrerCode} </b></p>}
            <button type="button" className="btn btn-danger mx-5 px-5">Block</button>
          </div>
        </div>
        {/* ... (rest of your HTML) */}
      </div>
    );
  };

  export default UserDetails;
